mapper.sql:
SELECT *
FROM TBL_CMN_CODE
WHERE GRP_ID = #{groupID}
AND CD_ID = #{code}

mapper.insert:
INSERT INTO TBL_CMN_CODE (
	GRP_ID
  , CD_ID
  , CD_VAL
  , DESCRP
  , UPD_ID
  , UPD_TIME
) VALUES (
	#{code.groupID}
  , #{code.code}
  , #{code.value}
  , #{code.description}
  , #{currentUser.id}
  , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE TBL_CMN_CODE SET
	CD_VAL = #{code.value}
  , DESCRP = #{code.description}
  , UPD_ID = #{currentUser.id}
  , UPD_TIME = CURRENT_TIMESTAMP
WHERE GRP_ID = #{code.groupID}
AND CD_ID = #{code.code}

mapper.delete:
DELETE FROM TBL_CMN_CODE
WHERE GRP_ID = #{groupID}

AND CD_ID IN (#{code})
