select.if:
SELECT SQL_CALC_FOUND_ROWS *, FOUND_ROWS() TOTAL_ROW_CNT
FROM TBL_GROUP
WHERE GRP_TYPE = #{groupType}

AND #{field} LIKE CONCAT(#{value}, '%')

AND STATUS <> '999' 
AND STATUS = #{status} 
ORDER BY GRP_ID
ORDER BY #{order}
LIMIT #{start}, #{fetch}

SELECT * FROM TBL_GROUP
WHERE GRP_TYPE = #{groupType}

AND #{field} LIKE CONCAT(#{value}, '%')

AND STATUS <> '999' 
AND STATUS = #{status} 
ORDER BY GRP_ID
ORDER BY #{order}

mapper.select:
SELECT *
FROM TBL_GROUP
WHERE GRP_TYPE = #{groupType}
AND GRP_ID = #{groupID}

SELECT LPAD(IFNULL(CAST(MAX(GRP_ID) AS UNSIGNED) + 1, 1), 3, '0') NEW_ID
FROM TBL_GROUP
WHERE GRP_TYPE = #{groupType}

mapper.delete:
DELETE FROM TBL_GROUP
WHERE GRP_TYPE = #{groupType}

AND GRP_ID IN (#{groupID})

mapper.insert:
INSERT INTO TBL_GROUP_MEMBER(GRP_TYPE, GRP_ID, MB_TYPE, MB_ID, INS_ID, INS_TIME)
SELECT #{groupType}, GRP_ID, #{memberType}, MB_ID, #{createdBy}, CURRENT_TIMESTAMP
FROM (
SELECT #{groupID} GRP_ID
) A, (
SELECT #{memberID} MB_ID
) B
WHERE NOT EXISTS (
	SELECT GRP_ID, MB_ID
	FROM TBL_GROUP_MEMBER C
	WHERE GRP_TYPE = #{groupType}
	AND MB_TYPE = #{memberType}
	AND C.GRP_ID = A.GRP_ID
	AND C.MB_ID = B.MB_ID
)

mapper.delete:
DELETE FROM TBL_GROUP_MEMBER
WHERE GRP_TYPE = #{groupType}
AND GRP_ID IN (#{groupID})
AND MB_TYPE = #{memberType}
AND MB_ID IN (#{memberID})

mapper.update:
UPDATE TBL_GROUP_MEMBER SET
SRT_ORD = CASE MB_IDWHEN #{memberID} THEN #{i} ELSE 99999 END
WHERE GRP_TYPE = #{groupType}
AND GRP_ID = #{groupID}
AND MB_TYPE = #{memberType}
